class PollutionDataset(Dataset):

    def __init__(self, frame):
        super().__init__()
        self.frame = frame

    def __len__(self):
        return self.frame.shape[0]

    def __getitem__(self, ind):
        x = torch.tensor(ind).float()
        y = self.frame.loc[ind, :].values.astype(np.float32)
        return x, y
