def gender_up(x):
    lst = []
    for i in x:
        if i == 'male' or i == 'female':
            lst.append(i)
        else:
            lst.append('group')
    return lst

kiva_combined['borrower_genders'] = gender_up(kiva_combined['borrower_genders'].to_list())
kiva_combined.borrower_genders.value_counts().reset_index()
