#compensation wrt years of experience and level of education for men/women
def experience_num(x):
    if x == 'I have never written code':
        return 0
    elif x == '<1 years':
        return 1
    elif x == '1-3 years':
        return 2
    elif x == '3-5 years':
        return 4
    elif x == '5-10 years':
        return 7
    elif x == '10-20 years':
        return 15
    elif x == '20+ years':
        return 25
    else:
        return 0

df_one['Q6_num'] = df_one['Q6'].apply(lambda x:experience_num(x))

