import random

wordsList = []  #Insert the words here if you'd like to use the modeZero (format: "firstWord"="secondWord")
                #Remove everything from the list if there's something to use the modeOne
repeatList = []

print("Welcome to my very simple test of your foreign language knowledge :)")

def menu():     

    print("Select mode by entering '0' for inserting the list into the code or '1' for just entering the words into the console.")
    mode = int(input())

    if mode == 0:

        modeZero()

    elif mode == 1:

        modeOne()

def perfection(firstList, secondList):

    while len(firstList) != 0:

        originalWord, translatedWord = str(firstList[random.randint(0, len(firstList) - 1)]).split("=")
        translation = originalWord + "=" + translatedWord

        print(originalWord)

        if translatedWord == input():

            print("Good job! ;)")

            firstList.remove(translation)

        else:

            print("It's %s. Try another one ;(" % (translatedWord))

            secondList.append(translation)
            firstList.remove(translation)

def modeOne():

    print("First enter the word in original language form and then the translated form.")
    print("If you are finished with inserting the words just enter 'q' two times in a row.")

    while "q=q" not in wordsList:

        wordsList.append(input() + "=" + input())

    wordsList.remove("q=q")
    modeZero()

def modeZero():

    print("Enter the translation:")

    while (len(wordsList) != 0) or (len(repeatList) != 0):

        if len(wordsList) != 0:

            perfection(wordsList, repeatList)

        elif len(repeatList) != 0:

            perfection(repeatList, wordsList)


    print("You know everything now B))")
    print("If you want to try again enter 'yes'.")

    if input() == "yes":
        menu()
    else:
        print("Goodbye then (:")


menu()
