# define functions
def convertTimestamp(dateTime,filterString):    
    toTimestampMethod = lambda s: np.int32(time.mktime(datetime.datetime.strptime(s, filterString).timetuple()))
    return toTimestampMethod(dateTime)

def convertTimestampDate(dateTime): 
    filterString = "%d/%m/%Y"
    return convertTimestamp(dateTime,filterString)

def convertTimestampTime(dateTime): 
    filterString = "%d/%m/%Y %H:%M:%S"
    return convertTimestamp(dateTime,filterString)

def convertDatetime(timeStamp):
    return datetime.datetime.fromtimestamp(timeStamp)

def getTimeFormatString(dateTime):
    strTime = str(dateTime.day) +"/" + str(dateTime.month) + "/" + str(dateTime.year) + \
            " " + \
            str(dateTime.hour) +":" + str(dateTime.minute) + ":" + str(dateTime.second) 
    return strTime
