def show_batch(image_batch, label_batch):
    plt.figure(figsize=(10,10))
    for n in range(16):
        ax = plt.subplot(4,4,n+1)
        plt.imshow(image_batch[n])
        if label_batch[n]:
            plt.title("ORGANIC")
        else:
            plt.title("RECYCLABLE")
        plt.axis("off")

# Visualize the first batch
show_batch(image_batch.numpy(), label_batch.numpy())
