def getGeneFrequency(status):
    rrs = meta[meta['viral_status']==status].index
    for i in range(len(rrs)):
        nums=expr.loc[:,[rrs[i]]].iloc[:,0]
        n=nums.idxmax()
        if n in maxes:
            maxes[n]+=1
        else:
            maxes[n]=1
    return maxes
