def clean_dates(row):
    '''
    This function cleans release_date row. 
    '''
    text = row["release_date"]
    yr = re.findall(r"\d+/\d+/(\d+)",text)

    if int(yr[0]) >= 18:
        return(text[:-2] + "19" + yr[0])
    else:
        return(text[:-2] + "20" + yr[0])

