def plot_series(time , series , format = "-" , start = 0, end = None , label = None , color = None):
    plt.plot(time[start:end] , series[start:end] , format , label = label , color = color)
    plt.xlabel("Time")
    plt.ylabel("Value")
    if label:
        plt.legend(fontsize = 14)
    plt.grid(True)

def trend(time , slope = 0 ):
    return slope * time

