class RandomZoom(object):

    def __call__(self, image):
        zoom_factor = np.random.uniform(0.7, 1.2)
        height = image.size[0]
        width = image.size[1]
        new_size = (np.int(zoom_factor*height), np.int(zoom_factor*width))
        return transforms.Resize(new_size)(image)

