#The following loop will run to collect all the data from the website. If provided with good connection, the loop will end after collecting all data(8ElementClickInterceptedException:)
while cond==True:
    try:
        #posted+job_title+company+city
        jb=driver.find_elements_by_xpath('//*[@id="root"]/div[3]/div/div/div/div/div[2]/div[3]/div/div/div/div/div')
        for i in jb:
            job.append(i.text)

        #job_requirement
        req=driver.find_elements_by_xpath('//*[@id="root"]/div/div/div/div/div/div[2]/div/div/div/div/div/section')
        for i in req:
            requirement.append(i.text)

        sleep(9)
        #pagination
        driver.find_element_by_xpath('//*[@id="root"]/div[3]/div/div/div/div/div[2]/div[3]/div/div[11]/nav/ul/li[9]/button').click()

        sleep(2)
    except NoSuchElementException:
        cond=False
    except ElementClickInterceptedException:
        cond=False
