def ypos2(v,t):
    g=9.81
    ls=2*v/g
    if t > 0 and t <= ls:
        y=v*t-0.5*g*t**2
    else:
        y="tiempo fuera de rango" 
    return (t,y)  

if __name__=='__main__':
    v0=7
    t0=0.714

t,y=ypos2(v0,t0)
print('A un tiempo t=%g s, la altura de la bola es %.2f m' % (t,y))

