nlp = spacy.load("en_core_web_lg")  # make sure to use larger model!
tokens = nlp("We will press forward with speed and urgency, for we have much to do in this winter of peril and possibility. Much to repair. Much to restore. Much to heal. Much to build. And much to gain.")

for token1 in tokens:
    for token2 in tokens:
        print(token1.text, token2.text, token1.similarity(token2))
