def recode_country(countries):
    if countries == 'Bolivia, Plurinational State of':
        return 'Bolivia'
    elif countries == 'Bolivia (Plurinational State of)':
        return 'Bolivia'
    elif countries == 'Bosnia and Herzegovina':
        return 'Bosnia Herzegovina'
