def cleaned_text(self, text):

    if self.lower:
        text = text.lower()


    if self.initial_filters is not None:
        text = re.sub(self.initial_filters, ' ', text)


    if self.remove_repetitions:
        pattern = re.compile(r"(.)\1{2,}", re.DOTALL)
        text = pattern.sub(r"\1", text)
    return text

