def cleaned_text(self, text):
    x = self._preprocess(text)
    for target, patterns in self.patterns.items():
        for pat in patterns:
            x = re.sub(pat, target, x)
    x = re.sub(r"[^a-z' ]", ' ', x)
    return x.split()
