def should_hit(player_total, dealer_card_val, player_aces):
    """Return True if the player should hit (request another card) given the current game
    state, or False if the player should stay. player_aces is the number of aces the player has.
    """

    if (player_aces == 0):

        #For high hands, always stand
        if (player_total >= 17):
            return False
        #For low hands, always hit
        if (player_total <= 11):
            return True
        elif(dealer_card_val<=3) & (player_total==12):
            return True
        #For dealer cards likely to bust
        elif (dealer_card_val <= 6):
            return False
        #Player between 10-18, dealer = 10
        else:
            return True 
    return False

    if (player_aces >0):
        if(player_total <= 17):
            return True
        elif(player_total==18) & (dealer_card_val >=9):
            return True
        else:
            return False
    return False

