games_lookup = {}

# i = 0
for idx, row in games.iterrows():
    # split devs and remove empty strings
    devs = row.developers.split(', ')
    if '' in devs:
        devs.remove('')    

    # publishers
    pubs = row.publishers.split(', ')
    if '' in pubs:
        pubs.remove('')

    # platforms
    plats = row.platforms.split(', ')
    if '' in plats:
        plats.remove('')

    # genres
    genres = row.genres.split(', ')
    if '' in genres:
        genres.remove('')

    games_lookup[idx] = {
        'developers': devs,
        'publishers': pubs,
        'platforms': plats,
        'genres': genres
    }
