# Function to remove the url
def remove_url(text):
    text = text.split(' ')
    text1 = ''
    for word in text:
        if ('.com' in word) or ('https' in word) or ('bit.ly' in word):
            continue
        else:
            text1 += (word+' ')
    return text1

# Lets apply this on news text
news['text'] = news['text'].apply(remove_url)
