def filtering(x):
    try:
        x = x.lower().split()
        if 'fresh' in x or 'healthy' in x:
            return '2 days'
        elif 'bad(cant' in x:
            return '2-5 hrs'
        elif 'average' in x:
            return 'day'
        else:
            return x
    except:
        return x
