import nltk

def get_sentences(docs, verbose=10000):

    for i, doc in enumerate(docs):
        for s in nltk.sent_tokenize(doc):
            yield s

        # print progress if needed
        if verbose > 0 and (i + 1) % verbose == 0:
            print(f"Progress: {i + 1}")

