class Perceptron():
    def __init__(self):
        self.w=None
        self.b=None

    def model(self,x):
        return(np.dot(self.w,x)>=self.b)

    def predict(self,X):
        Y=[]
        for x in X:
            result=self.model(x)
            Y.append(result)
        return np.array(Y)

    def fit(self,X,Y,epoch=1,lr=1):
        self.w=np.ones(X.shape[1])
        self.b=0
        accuracy={}
        max_accuracy=0
        wt_matrix=[]

        for i in range(epoch):
            for x,y in zip(X,Y):
                y_pred=self.model(x)
                if y==1 and y_pred==0:
                    self.w=self.w+lr*x
                    self.b=self.b+lr*1
                elif y==0 and y_pred==1:
                    self.w=self.w-lr*x
                    self.b=self.b-lr*1
            wt_matrix.append(self.w)
            accuracy[i]=accuracy_score(self.predict(X),Y)
            if accuracy[i]>max_accuracy:
                max_accuracy=accuracy[i]
                chkptw=self.w
                chkptb=self.b
        self.w=chkptw
        self.b=chkptb
        print(max_accuracy)
        plt.plot(list(accuracy.values()))
        plt.ylim([0,1])
        plt.xlim([0,epoch])
        return np.array(wt_matrix)
