def group_by_5_years(value):
    if value < 1976 :
        return "71-76"
    elif value < 1981:
        return "76-81"
    elif value < 1986:
        return "81-86"
    elif value < 1991:
        return "86-91"
    elif value < 1996:
        return "91-96"
    elif value < 2001:
        return "96-01"
    elif value < 2006:
        return "01-06"
    else:
        return "06-11"
