import plotly.graph_objs as go

xs = df_subject['Major']
ys = df_subject["Median Wage Early Career"]

data = [go.Bar(
    x=df_subject['Major'],
    y=df_subject["Median Wage Early Career"],
    marker={
        'color': ys,
        'colorscale': 'Viridis'
    }
)]
layout = {
    'xaxis': {
        'categoryorder': 'array',
        'categoryarray': [x for _, x in sorted(zip(ys, xs))]
    }
}
fig = go.FigureWidget(data=data, layout=layout)
fig.update_layout(
    font_family="Ariel",
    font_color="black",
    font_size = 8,
    title={
        'text': 'Early Career Median Wage by Major',
        'y':0.8,
        'x':0.5,
        'font_size' : 20,
        'xanchor': 'center',
        'yanchor': 'top'},
    xaxis_title={
        'text' : "Degree Major",
        'font_size' : 16
    },
    yaxis_title={
        'text' : "Annual Wage",
        'font_size' : 16
    }
)
fig.show()

