def value_counts(series, dropna=False, to_percentage=False):
    try:
        name = series.name
    except:
        pass
    visible_data = (series.value_counts(dropna=dropna).to_frame('count')
    .join(series.value_counts(normalize=True, dropna=dropna).to_frame('ratio')))
    try:
        visible_data.index.name = name
    except:
        pass
    if to_percentage:
        return visible_data.applymap(lambda x: '{:.1%}'.format(x) if isinstance(x, float) else x)
    else:
        return visible_data

