def get_bboxes(labels):
    opacities = labels.strip().split('opacity')
    opacity_list = []
    #print(opacities)
    for opacity in opacities:
        #print(opacity)
        if 'none' in opacity:
            continue
        if opacity != '':
            opacity_list.append(opacity.strip().split(' ')[1:])
    return opacity_list
