def num_turns_to_mine(rt_travel):
  #given the number of steps round trip, compute how many turns we should plan on mining
  if rt_travel <= 1:
    return 2
  if rt_travel <= 2:
    return 3
  elif rt_travel <=4:
    return 4
  elif rt_travel <=7:
    return 5
  elif rt_travel <=12:
    return 6
  elif rt_travel <=19:
    return 7
  elif rt_travel <= 28:
    return 8
  else:
    return 9

