class NegativeMarksError(Exception):
   pass

def check_marks(mark):
    try:

        if(mark < 0):
            raise NegativeMarksError

        if(mark > 50):
            print('pass')
        else:
            print('fail')
    except ValueError:
        print('ValueError: Some value error')
    except TypeError:
        print('TypeError: Cannot convert into int')
