def group_cabin(cabin):
    if cabin in ['A','B','C','T']:
        return 0
    elif cabin in ['D','E']:
        return 1
    elif cabin in ['F', 'G']:
        return 2
    elif cabin == 'U':
        return 3

for dataset in total:
    dataset['CabinGroup'] = dataset['Cabin'].apply(lambda x: group_cabin(x))

