def hops(l: list) -> bool:
    i = 0
    n = len(l)
    if n < 2:
        return True
    else:
        while i < n:
            if i == n-1:
                return i, True

            elif l[i] == 0:
                return i, False
            else:
                i += l[i]
                if i > n-1:
                    return i, False
