def full_report(y_test,y_hat,y_hat_probs,name = ''):
    if name != '':
        print(name)
    print(classification_report(y_test, y_hat))
    print("ROC AUC = ",roc_auc_score(y_test, y_hat_probs),'\n\n')

def roc_plot_label():
    plt.xlabel('False positive rate')
    plt.ylabel('True positive rate')
    plt.title('ROC curve')
    plt.legend(loc="best")
