class dataReader:
    def __init__(self, fileName):
        self.data = pd.read_csv(fileName)
        print("Column Names:", list(self.data.columns.values))
        print("Row count:", len(self.data))
        print("Number of Columns:", len(self.data.columns))

    def header(self,rownumber):
        return self.data.head(rownumber)

    def getByGender(self, genderr):
        try:
            return self.data[self.data['gender'] == genderr]
        except:
            print("No gender defined")

    def countByParentEducation(self, level):
        try:
            return print("Number of students whose parents are at {} education level: {}".format(
                level, len(self.data[self.data['parental level of education'] == level])))
        except:
            print("No such education level is defined")

    def getStatsByPrep(self, status):
        sc = self.data[self.data['test preparation course'] == status]
        sc = sc.select_dtypes(include=['int'])
        print("Min exam grades of students whose test pre-preparation status is {} : {}".format(status, sc.min() ))
        print("Max exam grades of students whose test pre-preparation status is {} : {}".format(status, sc.max() ))
        print("Average exam grades of students whose test pre-preparation status is {} : {}".format(status, sc.mean() ))

    def pvtable(self, score1, score2):
        return  self.data.pivot_table(values=[score1, score2] , 
                              index="race/ethnicity", 
                              columns="gender", fill_value=0, aggfunc=[np.min, np.max, np.mean])

    def nullRate(self, rate):
        """
        Enter whatever number you want the null rate to be greater than
        """
        for i in self.data.columns:
                null_rate = self.data[i].isna().sum() / len(self.data) * 100 
                if null_rate > rate :
                    print("{} null rate: {}%".format(i,round(null_rate,2)))

