from wordcloud import WordCloud
import matplotlib.pyplot as plt
import pandas as pd
import numpy as np
list=[]
print("Enter Name in words") 
while(1):
    tmp=input()
    if(tmp=="-1"):
        break
    elif(tmp==''):
        list.append(np.nan)
        continue
    else:
        list.append(tmp)
frame=pd.DataFrame(list,columns=['name']).dropna() 
var=frame['name']
try:
    if(plt.imshow(WordCloud(background_color='black', height=400, width=500, min_font_size=10).generate(' '.join(var))) and tmp=='-1'):
        plt.show()
except:
    print("Name Format ✍✍ is not correct!!!")
