def process_text(data):
    tokens = []
    for text in data:
        doc = nlp(text)
        temp = []
        for token in doc:
            if not token.is_stop and not token.is_punct:
                if not token.like_num:
                    temp.append(token.lemma_.lower().replace('\n',' '))
        tokens.append(' '.join(temp))

    return tokens

