nlp = spacy.load('en_core_web_sm')
nlp.Defaults.stop_words |= {'wine',"it's",'flavors','flavors.','now.','finish.','offers','shows','wine,', 'wine.','notes','aromas'}
def remove_stop(row):
    return [word for word in row if word not in nlp.Defaults.stop_words]
wine_tasting['new_cols'] = wine_tasting['description'].apply(lambda x: str(x).split()).apply(lambda x: remove_stop(x))
