for price in data.Price:
    if "Free" in price:
        prices_processed.append("Free")
        game_pass.append("No")

    elif "Full price" in price and "Game Pass" in price and "Included" not in price:
        if price.split()[-4] == "+":
            prices_processed.append(price.split()[-5])
        else:
            prices_processed.append(price.split()[-4])
        game_pass.append("Yes")
    elif "Full price" in price and "Game Pass" not in price and "Included" not in price:
        prices_processed.append(price.split()[-1])
        game_pass.append("No")
    elif "Included" in price and "Game Pass" in price:
        prices_processed.append("Included with Game Pass")
        game_pass.append("Yes")
    elif "Included" in price and "EA" in price:
        prices_processed.append("Included with EA Play")
        game_pass.append("No")
    else:
        prices_processed.append(price.split()[1])
        game_pass.append("Yes")
