kernel = np.array([1,-2,1])
def convolve(inp_array):
    inp_array = inp_array.values
    return np.convolve(kernel,inp_array,mode="same")

train["delta"] = np.vstack(train.groupby("breath_id")["u_in"].apply(convolve).values).flatten()

