def generateDCT(N):
    C = np.zeros([N,N], dtype=float)
    a = findNumber(N)
    for i in range(N):
        for j in range(N):
            k = ((2*j+1)*i)/N
            k = (math.pi/2)*k
            C[i][j] = round(a[i]*math.cos(k),3)
    return C

