def generateHadamardMatrix(N):
    H = np.ones([N,N])
    i=1
    while i<N:
        for j in range(i):
            for k in range(i):
                H[j+i][k] = H[j][k]
                H[j][k+i] = H[j][k]
                H[j+i][k+i] = -1*H[j][k]
        i += i
    return H

