def calculateWalshTransform(N):
    H = generateHadamardMatrix(N)
    W = np.zeros([N,N],dtype=int)
    temp = []
    for i in range(N):
        count = 0
        for j in range(N-1):
            if H[i][j]!=H[i][j+1]:
                count = count + 1
        temp.append(count)

    for i in range(N):
        W[temp[i]] = H[i]
    return W
