#function to categorize Magnitude
def desc(mag):
    if 0 <= mag <= 2.0:
        return 'Micro'
    elif 2.0 <= mag <= 3.9:
        return 'Minor'
    elif 4.0 <= mag <= 4.9:
        return 'Light'
    elif 5.0 <= mag <= 5.9:
        return 'Moderate'
    elif 6.0 <= mag <= 6.9:
        return 'Strong'
    elif 7.0 <= mag <= 7.9:
        return 'Major'
    elif 8.0 <= mag <= 9.9:
        return 'Great'
    else:
        return 'Epic'

#Applying the function to the Magnitude Column
data['Desc'] = data['Magnitude'].apply(lambda mag: desc(mag))
