def random_file_path(path):
    import os, random
    n=0
    random.seed()
    for root, dirs, files in os.walk(path):
        for name in files:
            n=n+1
            if random.uniform(0, n) < 1: 
                rfile=os.path.join(root, name)
    return rfile

