def speed_limit(speed, bd = False):
    if bd:
        speed -= 5
    if speed <= 60:
        return 'No Ticket'
    elif speed > 60 and speed <= 80:
        return 'Small Ticket'
    elif speed > 80:
        return 'Big Ticket'

print(speed_limit(65))
print(speed_limit(65, True))
