# Function to determine prime numbers:
def is_prime(n):
    if n > 2:
        i = 2
        while i ** 2 <= n:
            if n % i:
                i += 1
            else:
                return False
    elif n != 2:
        return False
    return True

#Create column in DF_cities to flag prime cities
DF_cities['IsPrime'] = DF_cities.CityId.apply(is_prime)

# Ok, let's preview the edited DF:
print(DF_cities.head(5))
