#Replce the acronyms with the actual words
def race(x):
    if(re.findall("W",x)):
        return 'White'
    elif(re.findall("B",x)):
        return 'Black'
    elif(re.findall("A",x)):
        return 'Asian'
    elif(re.findall("N",x)):
        return 'Native American'
    elif(re.findall("H",x)):
        return 'Hispanic'
    elif(re.findall("O",x)):
        return 'Other'

df['race']=df['race'].apply(lambda x:race(x))
