def remove_char(df):
    bad_var = ['per capita income', 'median household income', 'median family income', 'population', 'number of households']
    bad_tokens = ['$', r',']

    for var in bad_var:
        df[var] = df[var].replace('[\$,]', '', regex=True).astype(int)
    return df
