def to_letures_dict(df):
    da = {}
    for t in tqdm(df.itertuples(name=None), total=df.shape[0]):
        key = t[0]
        sub_dict = {}
        for i, col in enumerate(df.columns, 1):
            if col == "has_tags":
                sub_dict[col] = eval(t[i])
            else:
                sub_dict[col] = t[i]
        da[key] = sub_dict
    return da

