def geomean(data):
    index = 0
    N = len(data)
    result = 1
    for index in range(0, N):
        result = result * data[index]
    if N != 0:
        return abs(result) ** (1 / N)
    else:
        raise ValueError('No distribution has been passed in, cannot compute Geometric Mean')

