def harmonic_mean(data):
    index = 0
    N = len(data)
    result = 0
    for index in range(0, N):
        if data[index] != 0:
            result = result + (1 / data[index])

    if result == 0:
        raise ValueError('Distribution contains one or more zeros, cannot compute Harmonic Mean')
    else:
        return 1 / result

