def Limitspeed(speed):
    if speed < checkSpeedOfDay(61):
        print('No Ticket 👌')
    elif speed > 60 and speed < checkSpeedOfDay(80):
        print('Small Ticket 🎫')
    elif speed > checkSpeedOfDay(79):
        print('Big Ticket 🎫')

def checkSpeedOfDay(speed):
    # this is a creativity that in your birthday you can
    # accelerate 5 km more. other advantage is you can
    # change the speed limit by date
    # you can use date by: from datetime import date
    date = '2001-08-25'
    #date = date.today()
    BirthDay = '2001-08-25'
    if date == BirthDay:
        return speed + 5
    else:
        return speed

